                        SSTACK_JOBSR    STRING_FILL_BL, 9
#emac

#macro STRING_FILL_NB, 0
                        SSTACK_JOBSR    STRING_FILL_NB, 7
#emac

#macro STRING_INIT, 0
#emac

#macro STRING_IS_PRINTABLE, 1
                        CMPB    #$20
                        BLO     \1
                        CMPB    #$7E
                        BHI     \1
#emac

#macro STRING_MAKE_BL, 2
                        STRING_CALL_BL  \1, \2
                        SSTACK_PREPULL  2
                        RTS
#emac

#macro STRING_NL_NONTERM, 0
                        DB      STRING_SYM_CR
                        DB      STRING_SYM_LF
#emac

#macro STRING_NL_TERM, 0
                        DB      STRING_SYM_CR
                        DB      (STRING_SYM_LF|$80)
#emac

#macro STRING_PRINTABLE, 0
                        SSTACK_JOBSR    STRING_PRINTABLE, 2
#emac

#macro STRING_PRINT_BL, 0
                        SSTACK_JOBSR    STRING_PRINT_BL, 10
#emac

#macro STRING_PRINT_NB, 0
                        SSTACK_JOBSR    STRING_PRINT_NB, 8
#emac

#macro STRING_SKIP_AND_COUNT, 0
LOOP                    ADDD    #1
                        BRCLR   1,X+, #$80, LOOP
#emac

#macro STRING_UPPER, 0
                        SSTACK_JOBSR    STRING_UPPER, 2
#emac

#macro TIM_CLRIF, 1
                        TIM_MULT_CLRIF  (1<<\1)
#emac

#macro TIM_DIS, 1
                        TIM_MULT_DIS    (1<<\1)
#emac

#macro TIM_EN, 1
                        TIM_MULT_EN     ($1<<\1)
#emac

#macro TIM_INIT, 0
#emac

#macro TIM_MULT_CLRIF, 1
                        MOVB    #\1, TFLG1
#emac

#macro TIM_MULT_DIS, 1
                        BCLR    TIE, #\1
                        BNE     DONE
                        CLR     TSCR1
DONE                    EQU     *
#emac

#macro TIM_MULT_EN, 1
                        MOVB    #\1, TFLG1
                        BSET    TIE, #\1
                        MOVB    #(TEN|TSFRZ), TSCR1
#emac

#macro TIM_SET_DLY_D, 1
                        ADDD    TCNT
                        STD     (TC0+(2*\1))
#emac

#macro TIM_SET_DLY_IMM, 2
                        LDD     #\2
                        ADDD    TCNT
                        STD     (TC0+(2*\1))
#emac

#macro TIM_START_DLY, 1
                        BRSET   TIE, #(1<<\1), DONE
                        TIM_SET_DLY     \1
                        BSET    TIE, #(1<<\1)
                        MOVB    #(TEN|TSFRZ), TSCR1
DONE                    EQU             *
#emac

#macro VECTAB_INIT, 0
#emac

